import React, { Component } from 'react';
import axios from 'axios';
import { useParams } from 'react-router';

export default class ShowData extends Component {

    constructor(props){
        super(props);
        this.state={
            userList: []
        }
        this.id={
            showdataId: useParams()
        }
    }

    componentDidMount(){
        axios.get('http://localhost:3001/api/get').then((response)=>{
            //this.state.userList = response.data
            this.setState({
                userList: response.data
            })
        })
    }

    componentDidUpdate(){
        axios.get('http://localhost:3001/api/get').then((response)=>{
            //this.state.userList = response.data
            this.setState({
                userList: response.data
            })
        })
    }

    render() {
        return (
            <div>
                <h3>Requested showdata ID: {this.id.showdataId}</h3>
                {/* for testing */}
                {/* <h3>Current Users</h3>
                    {this.state.userList.map((val)=>{
                        return <h4>{val.username}, {val.password}, {val.firstName}, {val.lastName}, {val.email}</h4>
                    })} */}
            </div>
        )
    }
}
